import React, { Component } from "react";
const getNum = () => {
  return Math.floor(Math.random() * 10);
};
class NumPicker extends Component {
  render() {
    const num = getNum();
    return (
      <div>
        <h1>Your num is...{num}</h1>
        <p>{num === 7 ? "YOU WON" : "TRY AGAIN!"}</p>
        {num === 7 ? (
          <img src="https://media3.giphy.com/media/3o7aDfEAl370Fbgu40/giphy.gif?cid=ecf05e47m40dj43rnp8ldbrhzuz6v06mh5856xtbpdik8tgr&rid=giphy.gif&ct=g" />
        ) : null}
      </div>
    );
  }
}
export default NumPicker;
